function cleanDescriptionAndExtractUrl(text) {
  // B∆∞·ªõc 1: L√†m s·∫°ch URL l·∫∑p ki·ªÉu [url|url] ‚ûù url
  const cleaned = text.replace(/\[(https?:\/\/[^\|\]]+)\|\1\]/g, '$1');

  // B∆∞·ªõc 2: T√¨m URL ch∆∞a tr√πng
  const urlRegex = /https?:\/\/[^\s\|\]\)"]+/gi;
  const matches = cleaned.match(urlRegex);
  if (!matches) return { cleaned, extractedUrl: null };

  const uniqueUrl = matches.find(url => !seenUrls.has(url));
  if (uniqueUrl) seenUrls.add(uniqueUrl);

  return {
    cleaned,
    extractedUrl: uniqueUrl || null
  };
}

